import React from 'react';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import "./styling/Stickers.css";
import Button from '@material-ui/core/Button';

var stickers = [];

class Topic {
    constructor(height, width, bgc, color, titleSize, fontSize, id, topicName) {
      this.height = height;
      this.width = width;
      this.bgc = bgc;
      this.color = color;
      this.titleSize = titleSize;
      this.fontSize = fontSize;
      this.links = [];
      this.id = id;//use to set ids ending in different numbers for js to diffrenciate
      this.topicName = topicName

    }
    create(){
      //Sticker = element(box and text) + fulladder(input box + submit button)
      this.sticker = document.createElement("div")
      this.sticker.setAttribute('id', 'sticker'+this.id);
      //heading is the topic name
      this.heading = document.createElement("div");
      this.heading.setAttribute('id', 'stickerText'+this.id);
      //link adder = input box || link submitter = button right next to it
      this.linkAdder = document.createElement("input");
      this.linkSubmitter = document.createElement("button");
      //full adder is the input box and button in one unit
      this.fullAdder = document.createElement("div")
      this.fullAdder.setAttribute('id', 'fullAdder'+this.id);


      //Appending them to the correct divs
      document.body.appendChild(this.sticker);
      document.getElementById("sticker"+this.id).appendChild(this.heading)
      document.getElementById("sticker"+this.id).appendChild(this.fullAdder);
      document.getElementById("fullAdder"+this.id).appendChild(this.linkAdder);
      document.getElementById("fullAdder"+this.id).appendChild(this.linkSubmitter);

      //styling for color, size, and positioning of "this.element"
      this.sticker.style.height = this.height;
      this.sticker.style.width = this.width;
      this.sticker.style.backgroundColor = this.bgc;
      this.sticker.style.color = this.color;
      this.sticker.style.fontSize = this.fontSize;
      this.sticker.style.opacity = "60%";
      this.sticker.style.borderRadius = "15px"
      this.sticker.style.float = "right";
      this.sticker.style.textAlign = "center";
      this.sticker.style.marginBottom= "10%";
      this.sticker.style.marginRight= "13%";

      this.heading.innerHTML = this.topicName;

      //document.getElementById('sticker').onclick = function() { }
    }

  }
function createTopic(){
  let topicName = document.getElementById("stickersInput").value;
  document.getElementById("inputWrapper").style.visibility = "hidden";
  document.getElementById("stickersInput").value = "";

  let newTopic = new Topic("300px", "200px", "white", "black", "30px", "15px",stickers.length, topicName);
  newTopic.create();
  stickers.push(newTopic);
} 

function getTopicInfo(){
  document.getElementById("inputWrapper").style.visibility = "visible";
}

const Stickers = () => {
    return (
      <div id="stickersWrapper">
        <div id='inputWrapper'>
          <input id='stickersInput' placeholder="Enter topic name/link..."></input>
          <Button variant="contained" onClick={() => createTopic()} type="button" id="confirmBtn">Confirm</Button>
        </div>
        <AddCircleOutlineIcon color="primary" id="addBut" onClick={() => getTopicInfo()}></AddCircleOutlineIcon>
      </div>
    );
  };
  export default Stickers
