import React from 'react';
import './styling/SearchBar.css';
import Button from '@material-ui/core/Button';
import MuiAlert from "@material-ui/lab/Alert";

var searchCycle = 0;

const icons = [
  require("../images/googleIcon.png"),
  require("../images/ddgIcon.png"),
  require("../images/ytIcon.png")
]

const searchEngines = [
  "https://www.google.com/search?q=",
  "https://duckduckgo.com/?t=ffab&q=",
  "https://www.youtube.com/results?search_query=",
];

function openTab (i) {
  let query = document.getElementById("query").value;
  if(query.length > 0){
    window.location.replace(searchEngines[i]+query);
  }else{
    document.getElementById("searchStatus").style.visibility = "visible";
    document.getElementById("searchStatus").classList.add("vibrate");
    setTimeout(function () {
      document.getElementById("searchStatus").classList.remove("vibrate");
    }, 400);
  }

}

function cycle() {
  if(searchCycle == searchEngines.length - 1){
    searchCycle = 0;
  }else{
    searchCycle++;
  }

  document.getElementById("cycleButton").src = icons[searchCycle]
}

function handleKeys(e) {
   if(e.keyCode==13){
     openTab(searchCycle)
   }
}

const SearchBar = () => {
  return (
  <div>
<center id = "searchWrapper">
  <input type="text" id="query" name="q" placeholder="Search..." onKeyDown={(e) => handleKeys(e) }></input>
  <div id='buttons'>
  <img id="cycleButton" onClick={ () => cycle()} type="button" src={icons[searchCycle]} height={"40"} width={"40"}/>
  <Button id="searchButton" variant="contained" color="primary" onClick={ () => openTab(searchCycle)} type="button">Search</Button>
  </div>
</center>

  <MuiAlert elevation={6} variant="filled" severity="error" id="searchStatus">
  Please put something in the Search Bar!
  </MuiAlert>

  </div>
  );
};
export default SearchBar
