import React from 'react';
import './styling/Clock.css'

function formatTime (unixTime) {
  var time = [];

  var date = new Date(unixTime);
  var months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
  var hour = date.getHours()
  var min = date.getMinutes()
  var sec = date.getSeconds()

  if (hour > 12) {
    hour = hour - 12;
    periodOfDay = "PM";
  }
  var periodOfDay = "AM";

  if (hour.toString().length < 2) { hour = "0" + hour; }
  if (min.toString().length < 2) { min = "0" + min; }
  if (sec.toString().length < 2) { sec = "0" + sec; }

  // var time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec + " " + periodOfDay;
  time.push(date.getDate());
  time.push(months[date.getMonth()]);
  time.push(date.getFullYear());
  time.push(hour);
  time.push(min);
  time.push(sec);
  time.push(periodOfDay);

  return time;
}
function update (){
  let currentTime = formatTime(Date.now());
  document.getElementById("Clock").innerHTML = currentTime;
}

const Clock = () => {
  var timeUpdate = setInterval(() => {
    update();
  }, 1000);

  return (
      <div>
          <h2 id="Clock"></h2>
      </div>
    );

};

export default Clock;
