import { Dialog, DialogContent, DialogContentText, DialogTitle } from '@material-ui/core';
import React, { useState, useMemo, useCallback, useContext } from 'react';
import PropTypes from 'prop-types';

const ADialogContext = React.createContext({});

export function useADialog() {
    return useContext(ADialogContext);
}


export function ADialogProvider({ children, defaultTitle, defaultIcon, ...props}) {
    const [open, setOpen] = useState(false);
    const [title, setTitle] = useState('');
    // const [icon, setIcon] = useState(null);
    const [message, setMessage] = useState('');

    const showAlert = useCallback(
        (message, title) => {
            setMessage(message);
            setTitle(title ? title : defaultTitle);
            setOpen(true);
        },
        [ defaultTitle ]
    )

    const contextValue = useMemo(
        () => ({
            showAlert
        }),
        [ showAlert ]
    );

    return (
        <ADialogContext.Provider value={contextValue}>
            {children}
            <Dialog
                open={open}
                {...props}
            >
                {title && <DialogTitle>{title}</DialogTitle>}
                <DialogContent>
                    <DialogContentText>{message}</DialogContentText>
                </DialogContent>
            </Dialog>
        </ADialogContext.Provider>
    )
}

ADialogProvider.propTypes = {
    children: PropTypes.any.isRequired,
    defaultTitle: PropTypes.oneOfType([ PropTypes.string, PropTypes.node ]),
    defaultIcon: PropTypes.node,
}